import React,{Component} from 'react'
import ReactDOM from 'react-dom'
import './index.css'

class PopupDialogBox extends Component{
	constructor(props){
		super(props)
		this.state={
			shouldDisplay:this.props.display === 'block'?true:false
		}

	}
	closePermissionModification(){
		console.log('Clicked the close button !!')
		this.setState(() => {
			return {shouldDisplay:false}
		})
	}
	render(){
		let styleClass = ''
		if(this.state.shouldDisplay){
			styleClass = 'modal-show'
		}
		else{
			styleClass = 'modal'
		}
		
		return(
			<div className={styleClass}>			  
			  <div className="modal-content">
			    <span className="close" onClick={this.closePermissionModification}>&times;</span>
			    <p>Change File Permissions</p>
			  </div>

			</div>
		)
	}
	
}
export default PopupDialogBox
